// src/components/Navbar.js - Improved for mobile
import React, { useState, useEffect } from 'react';

/**
 * Responsive Navbar with accessible toggle for mobile and improved mobile handling
 */
const Navbar = () => {
  const [open, setOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [isSafari, setIsSafari] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Check for Safari browser and mobile devices
  useEffect(() => {
    // Check if using Safari
    const ua = navigator.userAgent.toLowerCase();
    setIsSafari(ua.indexOf('safari') > -1 && ua.indexOf('chrome') === -1);

    // Check if on mobile device
    setIsMobile(window.innerWidth <= 768);

    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Handle scroll event to change navbar style
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 20;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled]);

  // Lock body scroll when mobile menu is open
  useEffect(() => {
    if (open) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [open]);

  // Navbar class with special handling for Safari
  const navbarClass = `navbar ${scrolled ? 'navbar-scrolled' : ''} ${isSafari ? 'navbar-safari' : ''} ${isMobile ? 'navbar-mobile' : ''}`;

  return (
    <nav className={navbarClass} role="navigation" aria-label="Main Navigation">
      <div className="container navbar-content">
        <a href="#home" className="navbar-logo">Spanex</a>
        <button
          className="navbar-toggle"
          aria-label="Toggle navigation"
          aria-expanded={open}
          onClick={() => setOpen(!open)}
        >
          {open ? '✕' : '☰'}
        </button>
        <div className={`navbar-links${open ? ' open' : ''}`}>
          <div className="navbar-links-header">
            {isMobile && (
              <button
                className="navbar-close"
                aria-label="Close navigation"
                onClick={() => setOpen(false)}
              >
                ✕
              </button>
            )}
          </div>
          <a href="#home" className="navbar-link" onClick={() => setOpen(false)}>Home</a>
          <a href="#about" className="navbar-link" onClick={() => setOpen(false)}>About Us</a>
          <a href="#technology" className="navbar-link" onClick={() => setOpen(false)}>Technology</a>
          <a href="#impact" className="navbar-link" onClick={() => setOpen(false)}>Sustainability</a>
          <a href="#products" className="navbar-link" onClick={() => setOpen(false)}>Our Solutions</a>
          <a href="#contact" className="navbar-link" onClick={() => setOpen(false)}>Contact</a>
        </div>
      </div>

      {/* Overlay for mobile menu */}
      {open && (
        <div 
          className="navbar-overlay" 
          onClick={() => setOpen(false)}
          aria-hidden="true"
        ></div>
      )}
    </nav>
  );
};

export default Navbar;
