import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const AnimatedHero = () => {
  const [activeAnimation, setActiveAnimation] = useState(0);

  // Cycle through animations
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveAnimation((prev) => (prev + 1) % 4);
    }, 5000); // Change animation every 5 seconds

    return () => clearInterval(interval);
  }, []);

  // Function to get the mechanism name
  const getMechanismName = () => {
    switch(activeAnimation) {
      case 0: return "Respiration Control";
      case 1: return "Moisture Optimization";
      case 2: return "Microbial Inhibition";
      case 3: return "Oxidation Prevention";
      default: return "Respiration Control";
    }
  };

  return (
    <section className="hero-section" id="home">
      {/* Animated Background */}
      <div className="hero-background">
        {/* Respiration Control Animation */}
        <div className={`animation-layer respiration-animation ${activeAnimation === 0 ? 'active' : ''}`}>
          {Array.from({ length: 15 }).map((_, i) => (
            <motion.div 
              key={`resp-${i}`}
              className="circle-particle"
              initial={{ 
                opacity: 0,
                scale: 0,
                x: Math.random() * window.innerWidth, 
                y: Math.random() * window.innerHeight 
              }}
              animate={{ 
                opacity: activeAnimation === 0 ? [0, 0.7, 0] : 0,
                scale: activeAnimation === 0 ? [0, 1, 0] : 0,
                x: Math.random() * window.innerWidth, 
                y: Math.random() * window.innerHeight 
              }}
              transition={{ 
                duration: 8,
                repeat: Infinity,
                delay: i * 0.3
              }}
              style={{
                backgroundColor: '#2d5500',
                boxShadow: '0 0 10px 2px rgba(45, 85, 0, 0.3)'
              }}
            />
          ))}
        </div>

        {/* Moisture Optimization Animation */}
        <div className={`animation-layer moisture-animation ${activeAnimation === 1 ? 'active' : ''}`}>
          {Array.from({ length: 12 }).map((_, i) => (
            <motion.div 
              key={`moisture-${i}`}
              className="droplet-particle"
              initial={{ 
                opacity: 0,
                scale: 0,
                x: Math.random() * window.innerWidth, 
                y: -20
              }}
              animate={{ 
                opacity: activeAnimation === 1 ? [0, 0.7, 0] : 0,
                scale: activeAnimation === 1 ? [0, 1, 0] : 0,
                x: Math.random() * window.innerWidth,
                y: window.innerHeight + 20,
              }}
              transition={{ 
                duration: 10,
                repeat: Infinity,
                delay: i * 0.4
              }}
              style={{
                backgroundColor: '#87643e',
                borderRadius: '50% 50% 50% 0',
                transform: 'rotate(-45deg)',
                boxShadow: '0 0 10px 2px rgba(135, 100, 62, 0.3)'
              }}
            />
          ))}
        </div>

        {/* Microbial Inhibition Animation */}
        <div className={`animation-layer microbial-animation ${activeAnimation === 2 ? 'active' : ''}`}>
          {Array.from({ length: 20 }).map((_, i) => (
            <motion.div 
              key={`microbe-${i}`}
              className="microbe-particle"
              initial={{ 
                opacity: 0,
                scale: 0,
                x: Math.random() * window.innerWidth, 
                y: Math.random() * window.innerHeight,
                rotate: 0
              }}
              animate={{ 
                opacity: activeAnimation === 2 ? [0, 0.8, 0] : 0,
                scale: activeAnimation === 2 ? [0, 1, 0] : 0,
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                rotate: 360
              }}
              transition={{ 
                duration: 7,
                repeat: Infinity,
                delay: i * 0.2
              }}
              style={{
                backgroundColor: '#ff6e4e',
                borderRadius: '30% 70% 70% 30% / 30% 30% 70% 70%',
                boxShadow: '0 0 10px 2px rgba(255, 110, 78, 0.3)'
              }}
            />
          ))}
        </div>

        {/* Oxidation Prevention Animation */}
        <div className={`animation-layer oxidation-animation ${activeAnimation === 3 ? 'active' : ''}`}>
          {Array.from({ length: 15 }).map((_, i) => (
            <motion.div 
              key={`oxidation-${i}`}
              className="shield-particle"
              initial={{ 
                opacity: 0,
                scale: 0,
                x: window.innerWidth / 2 + (Math.random() * 100 - 50), 
                y: window.innerHeight / 2 + (Math.random() * 100 - 50),
              }}
              animate={{ 
                opacity: activeAnimation === 3 ? [0, 0.8, 0] : 0,
                scale: activeAnimation === 3 ? [0, 1, 0] : 0,
                x: (Math.random() - 0.5) * window.innerWidth + window.innerWidth / 2,
                y: (Math.random() - 0.5) * window.innerHeight + window.innerHeight / 2,
              }}
              transition={{ 
                duration: 6,
                repeat: Infinity,
                delay: i * 0.3
              }}
              style={{
                backgroundColor: '#fea201',
                borderRadius: '50%',
                boxShadow: '0 0 15px 3px rgba(254, 162, 1, 0.4)'
              }}
            />
          ))}
        </div>
      </div>

      {/* Dark overlay to improve text readability */}
      <div className="hero-overlay"></div>

      {/* Hero Content */}
      <div className="hero-content">
        <motion.h1 
          className="hero-title"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          Extending Nature's Freshness
        </motion.h1>

        <motion.p 
          className="hero-subtitle"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          Revolutionary patented <span style={{ color: '#ff6e4e', fontWeight: 600 }}>Spanex Shelf Max</span> formulations that extend produce shelf life by up to 5x, reducing losses for packhouses, growers, and retailers.
        </motion.p>

        <motion.div 
          className="hero-mechanism"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <span className="mechanism-caption">Featured Mechanism:</span>
          <div className="mechanism-name">{getMechanismName()}</div>
        </motion.div>

        <motion.div 
          className="hero-cta"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.9 }}
        >
          <a href="#products" className="btn btn-primary">Our Solutions</a>
          <a href="#contact" className="btn btn-outline">Schedule a Consultation</a>
        </motion.div>

        <motion.div 
          className="hero-disclaimer"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 1.2 }}
        >
          <small>
            Currently in testing phase â€“ not yet commercially launched. All ingredients are GRAS (Generally Recognized As Safe) certified.
          </small>
        </motion.div>
      </div>

      {/* Mechanism Indicators */}
      <div className="mechanism-indicators">
        {[0, 1, 2, 3].map((i) => (
          <button
            key={i}
            className={`mechanism-indicator ${activeAnimation === i ? 'active' : ''}`}
            onClick={() => setActiveAnimation(i)}
            aria-label={`Show ${['Respiration Control', 'Moisture Optimization', 'Microbial Inhibition', 'Oxidation Prevention'][i]} animation`}
          />
        ))}
      </div>
    </section>
  );
