import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';

const AnimatedHero = () => {
  const [activeAnimation, setActiveAnimation] = useState(0);

  // List of fruit and vegetable emojis with their properties
  const produceEmojis = [
    { emoji: "🍎", size: 40, rotation: 15 },    // Apple
    { emoji: "🍇", size: 35, rotation: -10 },   // Grapes
    { emoji: "🍓", size: 30, rotation: 5 },     // Strawberry
    { emoji: "🥑", size: 45, rotation: -5 },    // Avocado
    { emoji: "🫐", size: 25, rotation: 20 },    // Blueberries
    { emoji: "🍊", size: 35, rotation: -15 },   // Orange
    { emoji: "🍋", size: 32, rotation: 8 },     // Lemon
    { emoji: "🥭", size: 40, rotation: -8 },    // Mango
    { emoji: "🍍", size: 48, rotation: 12 },    // Pineapple
    { emoji: "🍑", size: 35, rotation: -12 },   // Peach
    { emoji: "🍉", size: 50, rotation: 5 },     // Watermelon
    { emoji: "🥝", size: 28, rotation: -5 },    // Kiwi
    { emoji: "🍈", size: 40, rotation: 10 },    // Melon
    { emoji: "🍐", size: 38, rotation: -10 },   // Pear
    { emoji: "🥕", size: 35, rotation: 25 },    // Carrot
    { emoji: "🥬", size: 40, rotation: -20 },   // Leafy Green
  ];

  // Cycle through animations
  useEffect(() => {
    const interval = setInterval(() => {
      setActiveAnimation((prev) => (prev + 1) % 4);
    }, 5000); // Change animation every 5 seconds

    return () => clearInterval(interval);
  }, []);

  // Function to get the mechanism name
  const getMechanismName = () => {
    switch(activeMechanism) {
      case 0: return "Respiration Control";
      case 1: return "Moisture Optimization";
      case 2: return "Microbial Inhibition";
      case 3: return "Oxidation Prevention";
      default: return "Respiration Control";
    }
  };

  // Function to get random emoji from the list
  const getRandomEmoji = () => {
    const randomIndex = Math.floor(Math.random() * produceEmojis.length);
    return produceEmojis[randomIndex];
  };

  // Generate static emoji array with random selections
  const generateStaticEmojiArray = (count) => {
    return Array.from({ length: count }).map(() => getRandomEmoji());
  };

  // Create a combined array of all emojis for static display
  const allEmojis = generateStaticEmojiArray(62); // Increased count for more fruits

  // Remove the conditional rendering based on activeAnimation state
  return (
    <section className="hero-section" id="home">
      {/* Static Background */}
      <div className="hero-background">
        {/* Static Fruit Display */}
        <div className="static-fruits-container">
          {allEmojis.map((item, i) => (
            <div 
              key={`fruit-${i}`}
              className="static-emoji"
              style={{
                fontSize: `${item.size}px`,
                filter: `drop-shadow(0 0 15px rgba(${
                  Math.floor(Math.random() * 255)}, ${
                  Math.floor(Math.random() * 255)}, ${
                  Math.floor(Math.random() * 255)}, 0.7))`,
                left: `${Math.random() * 95}%`,
                top: `${Math.random() * 95}%`,
                transform: `rotate(${item.rotation + Math.random() * 30}deg)`,
                position: 'absolute',
                zIndex: 1
              }}
            >
              {item.emoji}
            </div>
          ))}
        </div>
      </div>

      {/* Dark overlay to improve text readability */}
      <div className="hero-overlay"></div>

      {/* Hero Content */}
      <div className="hero-content">
        <motion.h1 
          className="hero-title"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          Extending Nature's Freshness
        </motion.h1>

        <motion.p 
          className="hero-subtitle"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.3 }}
        >
          Revolutionary patented <span style={{ color: '#ff6e4e', fontWeight: 600 }}>Spanex Shelf Max</span> formulations that extend produce shelf life by up to 5x, reducing losses for packhouses, growers, and retailers.
        </motion.p>

        <motion.div 
          className="hero-mechanism"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.6 }}
        >
          <span className="mechanism-caption">Featured Mechanism:</span>
          <div className="mechanism-name">{getMechanismName()}</div>
        </motion.div>

        <motion.div 
          className="hero-cta"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.9 }}
        >
          <a href="#products" className="btn btn-primary">Our Solutions</a>
          <a href="#contact" className="btn btn-outline">Schedule a Consultation</a>
        </motion.div>

        <motion.div 
          className="hero-disclaimer"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 1.2 }}
        >
          <small>
            Currently in testing phase – not yet commercially launched. All ingredients are GRAS (Generally Recognized As Safe) certified.
          </small>
        </motion.div>
      </div>

      {/* Mechanism Indicators */}
      <div className="mechanism-indicators">
        {[0, 1, 2, 3].map((i) => (
          <button
            key={i}
            className={`mechanism-indicator ${activeMechanism === i ? 'active' : ''}`}
            onClick={() => setActiveMechanism(i)}
            aria-label={`Show ${['Respiration Control', 'Moisture Optimization', 'Microbial Inhibition', 'Oxidation Prevention'][i]} information`}
          />
        ))}
      </div>
    </section>
  );
};

// CSS
const styles = `
  .hero-section {
    position: relative;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    background-color: rgba(13, 26, 0, 0.7);
  }

  .hero-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
  }

  .static-fruits-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
  }

  .static-emoji {
    display: block;
    line-height: 1;
    user-select: none;
  }

  .hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(13, 26, 0, 0.4);
    z-index: 1;
  }

  .hero-content {
    position: relative;
    z-index: 2;
    text-align: center;
    max-width: 800px;
    padding: 0 20px;
    color: white;
  }

  .hero-title {
    font-size: 3rem;
    margin-bottom: 1.5rem;
    color: white;
    text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
  }

  .hero-subtitle {
    font-size: 1.25rem;
    margin-bottom: 2rem;
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.9);
  }

  .hero-mechanism {
    margin-bottom: 2rem;
    padding: 0.75rem 1.5rem;
    background: rgba(45, 85, 0, 0.3);
    border: 1px solid rgba(45, 85, 0, 0.5);
    border-radius: 50px;
    display: inline-block;
  }

  .mechanism-caption {
    font-size: 0.875rem;
    opacity: 0.8;
    margin-right: 8px;
  }

  .mechanism-name {
    font-size: 1.25rem;
    font-weight: 600;
    color: #ffe02a;
  }

  .hero-cta {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    justify-content: center;
    margin-bottom: 2rem;
  }

  .btn {
    padding: 0.75rem 1.5rem;
    border-radius: 50px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    transition: all 0.3s ease;
  }

  .btn-primary {
    background-color: #ff6e4e;
    color: white;
    border: none;
  }

  .btn-primary:hover {
    background-color: #e85c36;
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(255, 110, 78, 0.3);
  }

  .btn-outline {
    background: transparent;
    color: white;
    border: 2px solid rgba(255, 255, 255, 0.6);
  }

  .btn-outline:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: white;
    transform: translateY(-2px);
  }

  .hero-disclaimer {
    font-size: 0.875rem;
    color: rgba(255, 255, 255, 0.7);
    max-width: 600px;
    margin: 0 auto;
  }

  .mechanism-indicators {
