// src/components/Navbar.js
import React, { useState, useEffect } from 'react';
import '../App.css';

/**
 * Responsive Navbar with accessible toggle for mobile and scroll behavior
 */
const Navbar = () => {
  const [open, setOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [scrollDirection, setScrollDirection] = useState('down');
  const [prevScrollY, setPrevScrollY] = useState(0);
  const [isHidden, setIsHidden] = useState(false);

  // Handle scroll event to change navbar style and position
  useEffect(() => {
    const handleScroll = () => {
      const currentScrollY = window.scrollY;

      // Determine if navbar should be scrolled style
      const isScrolled = currentScrollY > 20;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }

      // Determine scroll direction
      if (currentScrollY > prevScrollY) {
        // Scrolling down
        setScrollDirection('down');

        // Only hide navbar after scrolling down a bit (100px)
        if (currentScrollY > 100 && !isHidden) {
          setIsHidden(true);
        }
      } else {
        // Scrolling up
        setScrollDirection('up');

        // Show navbar when scrolling up
        if (isHidden) {
          setIsHidden(false);
        }
      }

      // Save current scroll position
      setPrevScrollY(currentScrollY);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrolled, prevScrollY, isHidden]);

  // Close mobile menu when clicking outside
  useEffect(() => {
    if (open) {
      document.body.style.overflow = 'hidden';

      const handleClickOutside = (e) => {
        if (open && !e.target.closest('.navbar-links') && !e.target.closest('.navbar-toggle')) {
          setOpen(false);
        }
      };

      document.addEventListener('mousedown', handleClickOutside);

      return () => {
        document.body.style.overflow = '';
        document.removeEventListener('mousedown', handleClickOutside);
      };
    } else {
      document.body.style.overflow = '';
    }
  }, [open]);

  return (
    <nav 
      className={`navbar ${scrolled ? 'navbar-scrolled' : ''} ${isHidden ? 'navbar-hidden' : ''}`} 
      role="navigation" 
      aria-label="Main Navigation"
    >
      <div className="container navbar-content">
        <a href="#home" className="navbar-logo">Spanex</a>
        <button
          className="navbar-toggle"
          aria-label="Toggle navigation"
          aria-expanded={open}
          onClick={() => setOpen(!open)}
        >
          <span className="navbar-toggle-icon"></span>
        </button>
        <div className={`navbar-links${open ? ' open' : ''}`}>
          <a href="#home" className="navbar-link" onClick={() => setOpen(false)}>Home</a>
          <a href="#about" className="navbar-link" onClick={() => setOpen(false)}>About Us</a>
          <a href="#technology" className="navbar-link" onClick={() => setOpen(false)}>Technology</a>
          <a href="#impact" className="navbar-link" onClick={() => setOpen(false)}>Sustainability</a>
          <a href="#products" className="navbar-link" onClick={() => setOpen(false)}>Our Solutions</a>
          <a href="#contact" className="navbar-link" onClick={() => setOpen(false)}>Contact</a>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
