// src/components/navigation/MainNav.jsx
import React from 'react';
import NavItem from './NavItem';

/**
 * Main navigation component for desktop view
 * 
 * @param {array} items - Navigation items with labels, links, and potential dropdowns
 * @param {string} activePage - Current active page
 */
const MainNav = ({ items = [], activePage = '' }) => {
  // Navigation items with updated structure
  const navItems = [
    { label: 'Home', href: '#home' },
    { 
      label: 'About Us', 
      href: '#about', 
      children: [
        { label: 'Company Overview', href: '#about' },
        { label: 'Food Waste Impact', href: '#problem' }
      ]
    },
    { 
      label: 'Technology', 
      href: '#technology', 
      children: [
        { label: 'Preservation Mechanisms', href: '#technology' },
        { label: 'Comparative Analysis', href: '#comparison' }
      ]
    },
    { label: 'Sustainability', href: '#impact' },
    { label: 'Our Solutions', href: '#products' },
    { label: 'Contact', href: '#contact' }
  ];

  return (
    <nav className="main-nav" aria-label="Main Navigation">
      <ul className="nav-list">
        {navItems.map((item, index) => (
          <NavItem
            key={index}
            label={item.label}
            href={item.href}
            children={item.children}
            isActive={activePage === item.href || activePage === item.label.toLowerCase()}
          />
        ))}
      </ul>
    </nav>
  );
};

export default MainNav;
