import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';

const SpanexFruitsShieldHero = () => {
  const [activeAnimation, setActiveAnimation] = useState(0);
  const [isRotating, setIsRotating] = useState(true);
  const rotationRef = useRef(null);

  // Mechanism data
  const mechanisms = [
    {
      id: 0,
      name: "Respiration Control",
      color: "#2d5500", // Dark Green
      icon: "üçÉ",
      description: "Regulates gas exchange and slows down ripening"
    },
    {
      id: 1,
      name: "Moisture Optimization",
      color: "#87643e", // Brown
      icon: "üíß",
      description: "Prevents dehydration while avoiding excess moisture"
    },
    {
      id: 2,
      name: "Microbial Inhibition",
      color: "#ff6e4e", // Coral/Orange
      icon: "ü¶†",
      description: "Creates inhospitable environment for bacteria and fungi"
    },
    {
      id: 3,
      name: "Oxidation Prevention",
      color: "#fea201", // Bright Orange
      icon: "‚öõÔ∏è",
      description: "Scavenges free radicals that cause oxidative damage"
    }
  ];

  // Fruit data for background animation
  const fruits = [
    { emoji: "üçé", size: 40, rotation: 15 },    // Apple
    { emoji: "üçá", size: 35, rotation: -10 },   // Grapes
    { emoji: "üçì", size: 30, rotation: 5 },     // Strawberry
    { emoji: "ü•ë", size: 45, rotation: -5 },    // Avocado
    { emoji: "ü´ê", size: 25, rotation: 20 },    // Blueberries
    { emoji: "üçä", size: 35, rotation: -15 },   // Orange
    { emoji: "üçã", size: 32, rotation: 8 },     // Lemon
    { emoji: "ü•≠", size: 40, rotation: -8 },    // Mango
    { emoji: "üçç", size: 48, rotation: 12 },    // Pineapple
    { emoji: "üçë", size: 35, rotation: -12 },   // Peach
    { emoji: "üçâ", size: 50, rotation: 5 },     // Watermelon
    { emoji: "ü•ù", size: 28, rotation: -5 },    // Kiwi
    { emoji: "üçà", size: 40, rotation: 10 },    // Melon
    { emoji: "üçê", size: 38, rotation: -10 },   // Pear
    { emoji: "ü•ï", size: 35, rotation: 25 },    // Carrot
    { emoji: "ü•¨", size: 40, rotation: -20 },   // Leafy Green
  ];

  // Auto-rotate mechanisms every 5 seconds and update active state
  useEffect(() => {
    if (isRotating) {
      const interval = setInterval(() => {
        setActiveAnimation((prev) => (prev + 1) % 4);
      }, 5000);

      return () => clearInterval(interval);
    }
  }, [isRotating]);

  // Function to pause rotation when hovering over mechanisms
  const pauseRotation = () => {
    setIsRotating(false);
  };

  // Function to resume rotation
  const resumeRotation = () => {
    setIsRotating(true);
  };

  // Function to handle mechanism click
  const handleMechanismClick = (id) => {
    setActiveAnimation(id);
    // Briefly pause rotation when a mechanism is clicked
    pauseRotation();
    setTimeout(resumeRotation, 10000); // Resume after 10 seconds
  };

  return (
    <section className="hero-section" id="home">
      {/* Animated Fruits Background */}
      <div className="fruits-background">
        {fruits.map((fruit, index) => (
          <motion.div
            key={`fruit-${index}`}
            className="floating-fruit"
            initial={{
              x: Math.random() * window.innerWidth,
              y: -100,
              rotate: fruit.rotation,
              opacity: 0
            }}
            animate={{
              y: window.innerHeight + 100,
              opacity: [0, 0.7, 0],
              scale: [0.6, 1, 0.8],
            }}
            transition={{
              duration: 15 + Math.random() * 10, // Random duration between 15-25s
              repeat: Infinity,
              delay: index * 2, // Stagger the animations
              ease: "linear"
            }}
            style={{
              fontSize: fruit.size,
              filter: "blur(2px)",
              position: "absolute",
              zIndex: 1
            }}
          >
            {fruit.emoji}
          </motion.div>
        ))}
      </div>

      {/* Dark Background */}
      <div className="hero-background">
        <div className="bg-overlay"></div>
      </div>

      {/* Content Container */}
      <div className="hero-content-container">
        {/* Main Content */}
        <div className="hero-text-content">
          <motion.h1 
            className="hero-title"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Extending Nature's Freshness
          </motion.h1>

          <motion.p 
            className="hero-subtitle"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.3 }}
          >
            Revolutionary patented <span className="highlight">Spanex Shelf Max</span> formulations 
            that extend produce shelf life by up to 5x, reducing losses for packhouses, growers, and retailers.
          </motion.p>

          <motion.div 
            className="hero-cta"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.9 }}
          >
            <a href="#products" className="btn btn-primary">Our Solutions</a>
            <a href="#contact" className="btn btn-outline">Schedule a Consultation</a>
          </motion.div>

          <motion.div 
            className="hero-disclaimer"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 1.2 }}
          >
            <small>
              Currently in testing phase ‚Äì not yet commercially launched.<br />
              All ingredients are GRAS (Generally Recognized As Safe) certified.
            </small>
          </motion.div>
        </div>

        {/* Shield Mechanism Visualization */}
        <div className="shield-visualization">
          <div className="shield-outer-ring">
            {/* Active Mechanism Label */}
            <div className="active-mechanism">
              <span>{mechanisms[activeAnimation].name}</span>
            </div>

            {/* Rotation Shield */}
            <div 
              className="shield-rotation" 
              ref={rotationRef}
              style={{
                animation: isRotating ? 'rotate 60s linear infinite' : 'none',
                transform: !isRotating ? `rotate(${90 - activeAnimation * 90}deg)` : undefined
              }}
            >
              {/* Mechanism Shields */}
              {mechanisms.map((mech, index) => {
                // Calculate position along the circle
                const angle = index * 90; // 90 degrees apart (4 items)
                const isActive = activeAnimation === mech.id;

                return (
                  <div
                    key={mech.id}
                    className={`mechanism-shield ${isActive ? 'active' : ''}`}
                    style={{
                      transform: `rotate(${angle}deg) translateY(-125px) rotate(-${angle}deg)`,
                    }}
                    onClick={() => handleMechanismClick(mech.id)}
                    onMouseEnter={pauseRotation}
                    onMouseLeave={resumeRotation}
                  >
                    <div 
                      className="mechanism-icon" 
                      style={{ 
                        backgroundColor: mech.color,
                        boxShadow: isActive ? `0 0 15px ${mech.color}` : 'none'
                      }}
                    >
                      <span>{mech.icon}</span>
                    </div>
                    <div className="mechanism-info">
                      <h3>{mech.name}</h3>
                      <p>{mech.description}</p>
                    </div>
                  </div>
                );
              })}

              {/* Connecting Lines (Shield Shape) */}
              <div className="shield-lines">
                <div className="shield-line line-0"></div>
                <div className="shield-line line-1"></div>
                <div className="shield-line line-2"></div>
                <div className="shield-line line-3"></div>
              </div>
            </div>
          </div>

          {/* Central Spanex Logo/Element */}
          <div className="spanex-center">
            <div className="spanex-logo">
              <div className="spanex-inner">
                <span className="spanex-text">SPANEX</span>
                <span className="spanex-subtext">TECHNOLOGY</span>
              </div>
              <div className="spanex-shield-glow"></div>
            </div>
          </div>
        </div>
      </div>

      {/* Protected Fruit Showcase */}
      <div className="protected-fruit-showcase">
        <div className="protected-badge">
          <span>Protected&nbsp;</span>
          <span className="protection-value">5√ó&nbsp;Longer</span>
        </div>
        <motion.div 
          className="fruit-icon-container"
          animate={{ rotateY: 360 }}
          transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
        >
          {/* Keep changing the showcase fruit based on active mechanism */}
          <span className="showcase-fruit">
            {["üçé", "ü•ë", "ü´ê", "üçì"][activeAnimation]}
          </span>
        </motion.div>
      </div>

      {/* Mechanism Indicators */}
      <div className="mechanism-indicators">
        {mechanisms.map((mech) => (
          <button
            key={mech.id}
            className={`mechanism-indicator ${activeAnimation === mech.id ? 'active' : ''}`}
            style={{ backgroundColor: activeAnimation === mech.id ? mech.color : 'rgba(255, 255, 255, 0.3)' }}
            onClick={() => handleMechanismClick(mech.id)}
            aria-label={`Show ${mech.name} information`}
          />
        ))}
      </div>
    </section>
  );
};

// CSS
const styles = `
  .hero-section {
    position: relative;
    min-height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    background-color: #0d1a00;
    padding: 2rem;
  }

  .hero-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(145deg, #10210a 0%, #0d1a00 100%);
    z-index: 0;
  }

  .bg-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(13, 26, 0, 0.85);
    z-index: 1;
  }

  .fruits-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 0;
    overflow: hidden;
  }

  .floating-fruit {
    filter: opacity(0.4);
  }

  .hero-content-container {
    position: relative;
    z-index: 2;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    gap: 4rem;
  }

  @media (min-width: 1024px) {
    .hero-content-container {
      flex-direction: row;
      gap: 2rem;
    }
  }

  .hero-text-content {
    flex: 1;
    text-align: center;
    color: white;
    max-width: 600px;
  }

  @media (min-width: 1024px) {
    .hero-text-content {
      text-align: left;
    }
  }

  .hero-title {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: white;
    text-shadow: 0 2px 10px rgba(0, 0, 0, 0.5);
  }

  @media (min-width: 768px) {
    .hero-title {
      font-size: 3rem;
    }
  }

  .hero-subtitle {
    font-size: 1.125rem;
    margin-bottom: 2rem;
    line-height: 1.6;
    color: rgba(255, 255, 255, 0.9);
  }

  @media (min-width: 768px) {
    .hero-subtitle {
      font-size: 1.25rem;
    }
  }

  .highlight {
    color: #ff6e4e;
    font-weight: 600;
  }

  .hero-cta {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem;
    justify-content: center;
    margin-bottom: 2rem;
  }

  @media (min-width: 1024px) {
    .hero-cta {
      justify-content: flex-start;
    }
  }

  .btn {
    padding: 0.75rem 1.5rem;
    border-radius: 50px;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-block;
  }

  .btn-primary {
    background-color: #ff6e4e;
    color: white;
    border: none;
  }

  .btn-primary:hover {
    background-color: #e85c36;
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(255, 110, 78, 0.3);
  }

  .btn-outline {
    background: transparent;
    color: white;
    border: 2px solid rgba(255, 255, 255, 0.6);
  }

  .btn-outline:hover {
    background: rgba(255, 255, 255, 0.1);
    border-color: white;
    transform: translateY(-2px);
  }

  .hero-disclaimer {
    font-size: 0.875rem;
    color: rgba(255, 255, 255, 0.7);
    max-width: 600px;
    margin: 0 auto;
  }

  @media (min-width: 1024px) {
    .hero-disclaimer {
      margin: 0;
    }
  }

  /* Shield Mechanism Visualization */
  .shield-visualization {
    position: relative;
    width: 100%;
    max-width: 500px;
    height: 500px;
    margin: 0 auto;
  }

  .shield-outer-ring {
    position: relative;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .active-mechanism {
    position: absolute;
    top: 20px;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.5);
    border-radius: 20px;
    padding: 0.5rem 1.5rem;
    color: white;
    font-weight: 600;
    font-size: 0.9rem;
    z-index: 10;
    backdrop-filter: blur(5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    transition: all 0.3s ease;
  }

  .shield-rotation {
    position: relative;
    width: 300px;
    height: 300px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: transform 0.5s ease;
  }

  @keyframes rotate {
    from {
      transform: rotate(0deg);
    }
    to {
      transform: rotate(360deg);
    }
  }

  .spanex-center {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 20;
  }

  .spanex-logo {
    width: 130px;
    height: 130px;
    background: linear-gradient(145deg, #2d5500, #1e3600);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    box-shadow: 0 0 20px rgba(45, 85, 0, 0.6), 
                inset 0 0 15px rgba(255, 255, 255, 0.1);
    border: 2px solid rgba(255, 255, 255, 0.1);
    position: relative;
  }

  .spanex-shield-glow {
    position: absolute;
    width: 200%;
    height: 200%;
    background: radial-gradient(ellipse at center, rgba(45, 85, 0, 0.4) 0%, rgba(45, 85, 0, 0) 70%);
    border-radius: 50%;
    animation: pulse 3s infinite alternate;
    z-index: -1;
  }

  @keyframes pulse {
    0% {
      opacity: 0.3;
      transform: scale(1);
    }
    100% {
      opacity: 0.6;
      transform: scale(1.2);
    }
  }

  .spanex-inner {
    background: linear-gradient(135deg, #2d5500 0%, #3a6f00 100%);
    width: 110px;
    height: 110px;
    border-radius: 50%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    border: 1px solid rgba(255, 255, 255, 0.1);
    position: relative;
    z-index: 2;
  }

  .spanex-text {
    color: white;
    font-weight: 800;
    font-size: 1.25rem;
    letter-spacing: 1px;
  }

  .spanex-subtext {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.75rem;
    letter-spacing: 0.5px;
  }

  /* Shield Lines */
  .shield-lines {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    pointer-events: none;
  }

  .shield-line {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 150px;
    height: 150px;
    border-radius: 50%;
    border: 2px solid rgba(255, 255, 255, 0.1);
    border-width: 2px 2px 0 0;
    transform-origin: center;
  }

  .line-0 {
    transform: rotate(0deg);
  }

  .line-1 {
    transform: rotate(90deg);
  }

  .line-2 {
    transform: rotate(180deg);
  }

  .line-3 {
    transform: rotate(270deg);
  }

  /* Mechanism Shields */
  .mechanism-shield {
    position: absolute;
    width: 150px;
    background: rgba(20, 20, 20, 0.8);
    backdrop-filter: blur(5px);
    border-radius: 10px;
    padding: 1rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    text-align: center;
    cursor: pointer;
    transition: all 0.3s ease;
    border: 1px solid rgba(255, 255, 255, 0.1);
    transform-origin: center;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
  }

  .mechanism-shield:hover {
    background: rgba(30, 30, 30, 0.9);
    transform: scale(1.05) rotate(-${1}deg) translateY(-125px) rotate(${1}deg) !important;
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
    z-index: 30;
  }

  .mechanism-shield.active {
    background: rgba(40, 40, 40, 0.95);
    transform: scale(1.1) rotate(-${0}deg) translateY(-125px) rotate(${0}deg);
    border: 1px solid rgba(255, 255, 255, 0.2);
    z-index: 25;
  }

  .mechanism-icon {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    margin-bottom: 0.75rem;
    transition: all 0.3s ease;
  }

  .active .mechanism-icon {
    transform: scale(1.1);
  }

  .mechanism-info h3 {
    color: white;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
    font-weight: 600;
  }

  .mechanism-info p {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.8rem;
    line-height: 1.4;
    margin: 0;
  }

  /* Mechanism Indicators */
  .mechanism-indicators {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 1rem;
    z-index: 30;
  }

  .mechanism-indicator {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    border: none;
    cursor: pointer;
    transition: all 0.3s ease;
  }

  .mechanism-indicator.active {
    transform: scale(1.2);
  }

  /* Protected Fruit Showcase */
  .protected-fruit-showcase {
    position: absolute;
    bottom: 30px;
    right: 30px;
    z-index: 25;
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  .protected-badge {
    background: rgba(45, 85, 0, 0.8);
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 30px;
    font-size: 0.9rem;
    margin-bottom: 0.75rem;
    backdrop-filter: blur(5px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
  }

  .protection-value {
    font-weight: 700;
    color: #ffe02a; /* Bright Yellow */
  }

  .fruit-icon-container {
    width: 60px;
    height: 60px;
    background: rgba(0, 0, 0, 0.2);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    backdrop-filter: blur(5px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3),
                0 0 20px rgba(45, 85, 0, 0.4);
  }

  .showcase-fruit {
    font-size: 2.5rem;
  }

  /* Responsive Adjustments */
  @media (max-width: 1023px) {
    .shield-visualization {
      height: 450px;
    }

    .shield-rotation {
      width: 270px;
      height: 270px;
    }

    .mechanism-shield {
      width: 130px;
    }

    .protected-fruit-showcase {
      bottom: 20px;
      right: 20px;
    }
  }

  @media (max-width: 767px) {
    .shield-visualization {
      height: 400px;
    }

    .shield-rotation {
      width: 250px;
      height: 250px;
    }

    .mechanism-shield {
      width: 120px;
      padding: 0.75rem;
      transform: rotate(${0}deg) translateY(-110px) rotate(-${0}deg) !important;
    }

    .mechanism-shield:hover,
    .mechanism-shield.active {
      transform: scale(1.05) rotate(${0}deg) translateY(-110px) rotate(-${0}deg) !important;
    }

    .mechanism-icon {
      width: 36px;
      height: 36px;
      font-size: 1.25rem;
    }

    .mechanism-info h3 {
      font-size: 0.8rem;
    }

    .mechanism-info p {
      font-size: 0.7rem;
    }

    .spanex-logo {
      width: 100px;
      height: 100px;
    }

    .spanex-inner {
      width: 85px;
      height: 85px;
    }

    .spanex-text {
      font-size: 1rem;
    }

    .spanex-subtext {
      font-size: 0.6rem;
    }

    .protected-fruit-showcase {
      right: 10px;
      bottom: 80px;
    }

    .protected-badge {
      font-size: 0.8rem;
      padding: 0.4rem 0.8rem;
    }

    .fruit-icon-container {
      width: 50px;
      height: 50px;
    }

    .showcase-fruit {
      font-size: 2rem;
    }
  }

  @media (max-width: 480px) {
    .hero-section {
      padding: 1rem;
    }

    .hero-title {
      font-size: 1.75rem;
    }

    .hero-subtitle {
      font-size: 1rem;
    }

    .hero-cta {
      flex-direction: column;
      width: 100%;
    }

    .btn {
      width: 100%;
      text-align: center;
    }

    .shield-visualization {
      height: 350px;
    }

    .shield-rotation {
      width: 220px;
      height: 220px;
    }

    .mechanism-shield {
      width: 100px;
      padding: 0.5rem;
      transform: rotate(${0}deg) translateY(-100px) rotate(-${0}deg) !important;
    }

    .mechanism-shield:hover,
    .mechanism-shield.active {
      transform: scale(1.05) rotate(${0}deg) translateY(-100px) rotate(-${0}deg) !important;
    }

    .protected-fruit-showcase {
      position: relative;
      bottom: auto;
      right: auto;
      margin-top: 20px;
    }
  }
